mapper.select:
select
id, user_id, title, content, create_time, creator, update_time, updator
from blogs


		and id = #{id}


		and user_id = #{userId}


		and title = #{title}


		and content = #{content}


		and create_time = #{createTime}


		and creator = #{creator}


		and update_time = #{updateTime}


		and updator = #{updator}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from blogs


		and id = #{id}


		and user_id = #{userId}


		and title = #{title}


		and content = #{content}


		and create_time = #{createTime}


		and creator = #{creator}


		and update_time = #{updateTime}


		and updator = #{updator}
