mapper.select:
select
id, blog_id, user_id, content, create_time, creator, update_time, updator, parent_id
from comments


		and id = #{id}


		and blog_id = #{blogId}


		and user_id = #{userId}


		and content = #{content}


		and create_time = #{createTime}


		and creator = #{creator}


		and update_time = #{updateTime}


		and updator = #{updator}


		and parent_id = #{parentId}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from comments


		and id = #{id}


		and blog_id = #{blogId}


		and user_id = #{userId}


		and content = #{content}


		and create_time = #{createTime}


		and creator = #{creator}


		and update_time = #{updateTime}


		and updator = #{updator}


		and parent_id = #{parentId}
