mapper.select:
select
university_id, university_name, province, city, district, location, ranking, type, level
from universities


		and university_id = #{universityId}


		and university_name = #{universityName}


		and province = #{province}


		and city = #{city}


		and district = #{district}


		and location = #{location}


		and ranking = #{ranking}


		and type = #{type}


		and level = #{level}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from universities


		and university_id = #{universityId}


		and university_name = #{universityName}


		and province = #{province}


		and city = #{city}


		and district = #{district}


		and location = #{location}


		and ranking = #{ranking}


		and type = #{type}


		and level = #{level}
