mapper.select:
select
id, universities_name, province, city, district, location, ranking, type, level
from universities


		and id = #{id}


		and universities_name = #{universitiesName}


		and province = #{province}


		and city = #{city}


		and district = #{district}


		and location = #{location}


		and ranking = #{ranking}


		and type = #{type}


		and level = #{level}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from universities


		and id = #{id}


		and universities_name = #{universitiesName}


		and province = #{province}


		and city = #{city}


		and district = #{district}


		and location = #{location}


		and ranking = #{ranking}


		and type = #{type}


		and level = #{level}
